package Lecture4;

public class practice2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<=10; i++) {
			//even number
			if(i%2==0) {
				System.out.println(i);
			}
		}
		for(int j=0; j<=10; j++) {
			//odd number
			if(j%2==1) {
				System.out.println(j);
			}
		}

	}

}
